<script>
import AppLayout from '@/Layouts/AppLayout'
import Welcome from '@/Jetstream/Welcome'
export default {
    props: [
        'question'
    ],
    components: {
        AppLayout,
        Welcome
    },
    data () {
      return {
        commentForm: this.$inertia.form({
          'body': ''
        }, {
          bag: 'createComment',
          resetOnSuccess: true
        }),
        resolveForm: this.$inertia.form({
            is_correct: true
        }, {
          bag: 'markCommentAsCorrect',
          resetOnSuccess: true
        }),
        applaudQuestionForm: this.$inertia.form({
            count: 1
        }),
        applaudCommentForm: this.$inertia.form({
            count: 1
        })
      }
    },
    methods: {
      createComment() {
        this.commentForm.post(route('questions.comments.store', this.question.id));
      },
      markCommentAsCorrect(id) {
          this.resolveForm.post(route('comments.resolve', id));
      },
      isQuestionPostedBy(user) {
          return this.question.user.id === user.id;
      },
      isCommentPostedBy(comment, user) {
          return comment.user.id === user.id;
      },
      applaudQuestion() {
          this.applaudQuestionForm.post(route('applauses.questions.store', this.question.id));
      },
      applaudComment(comment) {
          this.applaudCommentForm.post(route('applauses.comments.store', comment.id));
      }
    },
    mounted() {
        console.log(this.$page.user);
        console.log(this.question);
    }
}
</script>